import React,{useState,useEffect} from 'react'
import {useParams} from 'react-router-dom'
import { useSelector } from 'react-redux';
const EditTask = () => {
    const [task,setTask]=useState("")
    const {id}=useParams();
    const todos = useSelector(state =>state.TodoReducers.list)
    console.log(todos)
    /* const currentTodo=todos.find((ele)=>ele.id===id)
    console.log(currentTodo);
    useEffect(()=>{
       if(currentTodo){
           setTask(ele.task)
       }
    },[currentTodo])  */
    return (
        <>
       <div className="wrapper">
        <div className="form-wrapper">
            <div className="editheader">
              <h1>Edit Task for {id}</h1>
              </div>
              <br></br>
              <form className="form-group" >
                <br></br>
                <label>Task</label>
                <input type="text" className="form-control" name="edittask" value={task} onChange={(e)=>setTask(e.target.value)}></input>
                <br></br>
                <div className="btns">
                <button type="submit" className="btedit">Save</button>
                <button type="submit" className="btncancel">Cancel</button>
                </div>
                <br></br>
            </form>
           </div>
        </div>
        </>
    )
}

export default EditTask
